//用秘钥发送非合约交易
var sendRawTransaction = function(){
    var Tx = require('ethereumjs-tx');
    var privateKey = new Buffer('a6abcae8af8089b782b90ee864da0912320d31a83306b740d1a843108e8cfa9f', 'hex');
    var latestNonce = web3.eth.getTransactionCount("0xeb0e5b3c177c72235d07d8daa6e7ad8fe701e24a");//ttt 应该改成账号的nonce而不是区块中的交易数
    console.log("latestNonce:",latestNonce);
    var rawTx = {
        nonce: latestNonce+1,
        gasPrice: 180000,
        gasLimit: 1000000000000,
        gas: 100000000,
        to: "0xc17f762282149f36f2b26bfee54aa8678b70c3a4",
        value: 1,
        data: ''
    }

    var tx = new Tx(rawTx);
    tx.sign(privateKey);
    var serializedTx = tx.serialize();
    // console.log(serializedTx.toString('hex'));
    web3.eth.sendRawTransaction("0x"+serializedTx.toString('hex'), function(err, hash) {
    if (!err)
        console.log(hash); // "0x7f9fade1c0d57a7af66ab4ead79fade1c0d57a7af66ab4ead7c2c2eb7b11a91385"
    else
        console.log(err); 
    });
}

